INSERT INTO `scienceactivity` VALUES ('190', '1', '  感知千年盐都', '2015年6月4日', '博物馆', '', '走进博物馆“感知千年盐都”科普参观活动。四川师范大学的师生们走进博物馆，感受千年盐都的独特魅力，了解千年盐都的历史。', '自贡市盐业历史博物馆', '参加人数：137', '', null, null, null);
