INSERT INTO `scienceactivity` VALUES ('195', '1', '感知千年盐都', '2015年7月3日', '博物馆', '', '走进博物馆“感知千年盐都”科普参观活动。7月3日上午，舒平小学近80名留守儿童走进博物馆，参观中国井盐科技史陈列，感受古代盐工巧夺天工的盐场技艺，体验盐业历史魅力。', '自贡市盐业历史博物馆', '参加人数：78', '', null, null, null);
